hooks.body:
if (!request_vendor_parameters.pageCursor) {
	var newBody = {
	  "startDate": request_parameters.startDate,
	  "endDate": request_parameters.endDate,
	  "pageSize": request_parameters.pageSize
	};
	done({
	  "request_vendor_body": newBody
	});
} else {
	let cursor = request_vendor_parameters['pageCursor'];
	if (cursor.indexOf("CESID") > -1) {
		let pageCursor = cursor.split("CESID")[0];
		let searchId = cursor.split("CESID")[1];
		let map = {
			"pageCursor": pageCursor
		};
		var path = request_vendor_path + '/' + searchId;
		done({
			"request_vendor_parameters": map,
			"request_vendor_method": "GET",
			"request_vendor_path": path
		});
	} else {
		done();
	}
}

if (response_body.message === "No agreement asset events in your account" | response_body.code === "NO_AGREEMENT_ASSET_EVENTS"){
	var response = {
		"events":[]
	}
	done({
		"response_status_code": 200,
		"response_body": response
	});
}
if (response_body.nextPageCursor) {
	let cursor_id = response_body.nextPageCursor;
	let search_id = response_body.searchId;
	response_body.nextPageCursor = cursor_id + 'CESID' + search_id;
	done({
		"response_body": response_body	
	});
} else {
	done()
}

if (!response_iserror) {
  var response = {};
  response['endpoint'] = 'adobe-esign';
  response['dateTime'] = new Date();
  response['valid'] = 'true';
  done({
  	"response_body": response
  });
}

done({
"continue":true
})

var expressionQuery = request_expression; 
var request_param = request_vendor_parameters; 

if (expressionQuery !== null) { 
 if (!request_param) { 
 request_param = {}; 
 } 
 for (var i = 0; i < expressionQuery.length; i++) { 
 var key = expressionQuery[i].attribute; 
 var value = expressionQuery[i].value; 
 if (key == 'email') { 
 request_param['x-user-email'] = value 
 request_param['email'] = null; 

 } else { 

 request_param[key] = value; 
 } 
 } 
} 

done({ 
 "request_vendor_parameters": request_param 

})

if ((request_parameters !== null && !request_parameters.apiUser && configuration['adobe-esign.api.user']) || configuration['adobe-esign.api.user']) {
		let map = {
			'x-api-user': configuration['adobe-esign.api.user']
		};
		done({
			"request_vendor_headers": map
		});
	}
done();
