hooks.body:
if(!request_expression) done({});
var exp = request_expression;
if (exp.length > 1) {
	done({
    continue: false,
		response_error: 'Only 1 filter is supported per request',
    response_status_code: 400
  })
}
let params = {};
if (exp[0].attribute === 'reference') {
  params.reference = exp[0].value;
} else {
	params.q = exp[0].value;
}

done({request_vendor_parameters: params})

var expressionQuery = request_expression;

if (expressionQuery === null || expressionQuery === undefined) {
    done({ 
      "request_vendor_path": vendorUrl
      });
}
else{
var filterString='';
for (var i = 0; i < expressionQuery.length; i++) {
    var key = expressionQuery[i].attribute;
    var operator = expressionQuery[i].operator;
    var value = expressionQuery[i].value;
    if(i < expressionQuery.length-1)
    filterString +=  key+operator+value+'&';
    else
    filterString +=  key+operator+value;
}
  var vendorUrl =  request_vendor_path+'?'+encodeURI(filterString);



done({
    "request_vendor_path": vendorUrl
});
}

const getCustomFields = (body) => {
	let fields = body.fields.filter(field => field.custom && field.custom === true);
  return {
		fields
  }
};

const chargifyTypes = {
  'text': 'string',
  'radio': 'boolean',
  'dropdown': 'string'
};

const getFieldFromMetaField = (metaField) => {
  let field = {};
	if (metaField.name) {
    field.vendorPath = metaField.name;
  }
  if (metaField.input_type) {
    let inputType = metaField.input_type;
 	  field.vendorNativeType = inputType;
    field.type = chargifyTypes[inputType];
    if (inputType === 'dropdown' || inputType === 'radio') {
      let pickList = null;
      if (metaField.enum) {
        pickList = metaField.enum.map(key => {
          let tmp = {};
          tmp.name = key;
          tmp.value = key;
          return tmp;
        });
     }

     if (pickList) {
	     field.picklistValues = pickList;       
     }
   }
  }
  field.custom = true;
  return field;
};

const customFields = request_parameters && (request_parameters['customFieldsOnly'] == 'true' || request_parameters['customFieldsOnly'] == true );
if (!response_iserror) {
  let fields = response_body.map(obj => getFieldFromMetaField(obj));
  if (customFields) {
    done({
			response_body: { fields }
    });
  } else {
    if (meta_data) {
			fields = meta_data.fields.concat(fields);
      done({
        response_body: { fields }
      });
    } else {
      done({
        response_body: { fields }
      });
    }
  }
} else {
  if (meta_data) {
    done({
      response_body: customFields ? getCustomFields(meta_data) : meta_data,
      response_status_code: 200
    });
  } else {
    let requestPathVariables = request_path.split('/');
    let objectName = requestPathVariables[requestPathVariables.length - 2]; 
    done({
      response_body_raw: `No object metadata found for ${objectName}`,
      response_body: { error: `No object metadata found for ${objectName}`},
      response_status_code: 404
    });
  }
}

let vendorMapping = {
  'chargifyEvents': 'events',
  'customers': 'customers',
  'invoices': 'invoices',
  'products': 'products',
  'productFamilies': 'product_families',
  'productFamiliesProducts': 'products',
  'subscriptions': 'subscriptions',
  'subscriptionsTransactions': 'transactions',
  'transactions': 'transactions'
};

// Give me the objectName here...
let requestPathVariables = request_path.split('/');
let objectName = requestPathVariables[requestPathVariables.length - 2]; 
objectName = vendorMapping[objectName] || objectName;

let path = `/${objectName}/metafields.json`;

done({
  "request_vendor_path": path
});

//Normalizing response to return empty body
if(!response_iserror){
   let respbody ={};
  done({
		"response_body": respbody
	})	
}
done();

let new_body = [];
for(var i=0;i<response_body.length;i++)
{
  for (let key in response_body[i])
{	var sub_body={};
	if (key === "transaction") 
	{			
    	for(let subKey in response_body[i][key])
		sub_body[subKey] = response_body[i][key][subKey];	
	} 
}  
new_body[i]=sub_body;
}
done({"response_body": new_body,
	  "continue": true	});
