const variableExists = (variable) => (variable!==undefined && variable!==null);
const acceptableCodes = [400, 401, 403, 404, 405, 406, 409, 415, 500, 502];

let response = {};

if(variableExists(response_body)){

  if(variableExists(response_body.responseCode)){
    //Extract the code, if the response body contains the response code
    if(response_body.responseCode>=200 && response_body.responseCode<300){
      response.response_status_code=200;
    } else if(response_body.responseCode>=300 && response_body.responseCode<400){
      response.response_status_code=300;
    } else if(response_body.responseCode>=400 && response_body.responseCode<500){
      if(acceptableCodes.includes(response_body.responseCode))
        response.response_status_code=response_body.responseCode;
      else
        response.response_status_code=400;
    } else if(response_body.responseCode>=500){
      if(acceptableCodes.includes(response_body.responseCode))
        response.response_status_code=response_body.responseCode;
      else
        response.response_status_code=500;
    } else {
      response.response_status_code=response_body.responseCode;
    }
    delete response_body.responseCode;
  }

  response.response_body = response_body;
}

done(response);
