var conf= configuration ;
var apiKey = conf["api.key"];
conf['username'] = apiKey;
done({
	"configuration": conf
	});

var attachmentBody;
var headers =request_vendor_headers;
var user_id = "";
try {
	attachmentBody = JSON.parse(request_vendor_body);
} catch (e) {
	done();
}

var jsonBody = {};
if(attachmentBody.url){
  jsonBody=attachmentBody;
  jsonBody.type=attachmentBody.attachment_type;
}else{
  jsonBody.content = attachmentBody.body;
  jsonBody.filename = attachmentBody.name;
  jsonBody.content_type = attachmentBody.type;
  jsonBody.type = attachmentBody.attachment_type;

}
user_id=attachmentBody.user_id;
headers["On-Behalf-Of"] = user_id.toString();
done({
  "request_vendor_headers":headers,
	"request_vendor_body": jsonBody
});

  var formattedEvents = []; 
  var eventObj = {}; 
  var eventBody = events;
  var type = eventBody.action;
  var bodyData = eventBody['payload'];

  if (type === 'delete_candidate') {
  	 var personData = bodyData.person;
     handleEvent("DELETED",personData.id,"CANDIDATE",personData.created_at);
  }else if (type === 'job_created') {
     var job=bodyData.job;
     handleEvent("CREATED",job.id,"JOB",job.created_at);
  } else if (type === 'hire_candidate') {
     var offerData=bodyData.application.offer;
	   handleEvent("UPDATED", offerData.id, "CANDIDATE", offerData.created_at);
  } else if (type === 'candidate_stage_change' || type === 'unhire_candidate' || type === 'reject_candidate' || type === 'unreject_candidate'){
	   var candidate=bodyData.application.candidate;
     handleEvent("UPDATED", candidate.id, "CANDIDATE", candidate.created_at);
  } else if (type === 'update_candidate') {
     var candidate=bodyData.candidate;
	   handleEvent("UPDATED", candidate.id, "CANDIDATE", candidate.created_at);
  } else if (type === 'job_deleted') {
     var job=bodyData.job;
	   handleEvent("DELETED", job.id, "JOB", "null");
  } else if (type === 'job_updated' || type === 'job_approved') {
     var job=bodyData.job;
  	 handleEvent("UPDATED", job.id, "JOB", job.created_at);
  } else if (type === 'job_post_created') {
     var job=bodyData;
	   handleEvent("CREATED", job.id, "JOBPOST", job.updated_at);
  } else if (type === 'job_post_updated') {
      var job=bodyData;
	   handleEvent("UPDATED", job.id, "JOBPOST", job.updated_at);
  } else if (type === 'job_post_deleted') {
     var job=bodyData.job_post;
  	 handleEvent("DELETED", job.job_id, "JOBPOST", job.updated_at);
  } else if (type === 'job_interview_stage_deleted') {
     var job=bodyData.job_interview_stage;
	   handleEvent("DELETED", job.job_id, "JOBPOST", job.updated_at);
  }
  function handleEvent(eventType, objectId, ObjectType, eventDate){
	   eventObj.event_type = eventType;
     eventObj.event_object_id = objectId;
     eventObj.event_object_type = ObjectType;
     eventObj.event_date = eventDate;
  }

  formattedEvents.push(eventObj);

done({    
	"events" : formattedEvents    
})
