var vendorQuery = {};

var page = request_parameters.page;
var pageSize = request_parameters.pageSize;
var offset = (page-1) * pageSize;
vendorQuery.offset = offset;


done({  
	request_vendor_parameters: vendorQuery,  
	continue: true 
});

if(response_status_code != 200){
	if (response_body.message && response_body.message.indexOf('not found') !== -1){
		done({
			response_status_code: 404
		});
	}
}

done();

// We do this so that the data returned is valid. 
// this api returns valid data on failure. So in order for 
// EB to work, we set the code to 200. and look for fail message.


if(response_status_code === 400)
{
	done({response_status_code:200});
}
done();

var updatedBody = [];

for (var i = 0; i < response_body.length; i++) {
	updatedBody.push({
		lswUuid: response_body[i]
	});
}

done({
"response_body": updatedBody
});
