let headers = {};
let b64 = CE.b64(configuration['recurly.api.key']);

headers.Authorization = 'Basic ' + b64;
done({
	"request_vendor_headers": headers
});

let Link = '';
if (response_headers.Link) {
  Link = response_headers.Link;
  if (Link.includes(',')) {
    let links = Link.split(',');
    for (var i = 0; i < links.length; i++) {
      if (links[i].includes('rel="next"')) {
        Link = links[i];
      }
    }
  }
}
Link = Link.replace('<', '');
Link = Link.replace('>; rel="next"', '');
Link = decodeURIComponent(Link);

let new_response_headers={
	"Link":Link
};

done({
  "response_headers": new_response_headers
});

if (request_parameters.orderBy) {
	let map = {};
	let orderBy = request_parameters.orderBy;
	let fieldName = orderBy.split(' ')[0];
	let order = orderBy.split(' ')[1];
	map.order = order.trim();
	map.sort = fieldName.trim();
	done ({
		"request_vendor_parameters": map
	});
} else {
	done();
}

