let vendor_path = request_vendor_path;
if(configuration['store.url'].indexOf('v3') > -1){
	vendor_path = vendor_path + '/subscribers';
}
done({
	request_vendor_path: vendor_path
});

var request = request_body_map;

if(request && request.shipping_addresses && !Array.isArray(request.shipping_addresses)){
  let new_shipping_addresses = [];
  new_shipping_addresses.push(request.shipping_addresses);
  request['shipping_addresses'] = new_shipping_addresses;
} 

if(request && request.order_products){
  request['products'] = request.order_products;
  delete request.order_products;
}

done({
  request_vendor_body: request 
});

var request = request_body_map;

if(request && request.shipping_addresses && !Array.isArray(request.shipping_addresses)){
  let new_shipping_addresses = [];
  new_shipping_addresses.push(request.shipping_addresses);
  request['shipping_addresses'] = new_shipping_addresses;
} 

if(request && request.order_products){
  request['products'] = request.order_products;
  delete request.order_products;
}

done({
  request_vendor_body: request 
});

var vendor_headers = request_vendor_headers;
if(configuration ['authentication.type']=='basic'){ 
    var vendor_headers = request_vendor_headers;
    if(vendor_headers) {
		delete request_vendor_headers['X-Auth-Client'];
		delete request_vendor_headers['X-Auth-Token'];
	}
}

done({
	continue: true,
	request_vendor_headers: vendor_headers
})
