function singularize(value) {
	if (value.substring(value.length - 3) === 'ies') {
		// If a value ends in ies (opportunities), you drop the ies and add -y to make it singluar. 
		return value.slice(0, -3) + 'y';
	}
	else if (value.substring(value.length - 1) === 'i') {		
		// If a value ends in i (cacti), you drop the i and add -us to make it singular. 
		return value.slice(0, -1) + 'us'; 
	}
	else if (value.substring(value.length - 2) === 'es') {
	 	// If a value ends in es (tomatoes), you drop the es to make it singular
		return value.slice(0, -2);
	}
	else if (value.substring(value.length - 1) === 's') {
		// If a value ends in s (apples), you drop the s to make it singular. 
		return value.slice(0, -1);
	}
	else {
		// anything else, don't do anything			
		return value;
	}
}
function parseResponse(rawResponse, entityName) {
	if (response_iserror) { return rawResponse; }
	var singularEntityName = singularize(entityName);
	var attributes = [];
	var activityEntities = [
		'tasks',
		'activitypointers',
		'opportunitycloses',
		'quotecloses',
		'ordercloses'
	];
	var isActivity = activityEntities.indexOf(entityName) !== -1;
	if (rawResponse) {
	    rawResponse.map(function(obj) {
	    	var idPropertyName = isActivity ? 'activityid' : singularEntityName + 'id';
			// Updated this part to predict for other possible ID field by removing 1 character when not found
			// Otherwise look for activityid existence because we have not discovered all possible activity entities
	    	obj.id = obj[idPropertyName] || obj[entityName.slice(0, -1) + 'id'] || obj.activityid || '';
	    	attributes.push(obj);
	    });
	}
	return attributes;
}
