    });
  }
} else if (!response_body || !response_body.latest_id) {
  done({
    "response_status_code": 200,
    "response_body": []
  });
} else {
  configuration.last_event_id = parseInt(response_body.latest_id);

  var events = [];

  for (var i = 0; i < parseInt(response_body.count + ""); i++) {
    var eve = {
      "event_date": response_body.events[i].timestamp + "", //ISO date time format
      "event_type": null, //can be INIT,CREATED,RETRIEVED,UPDATED,MOVED, DELETED, UNKNOWN
      "event_object_id": null, //Id value of the resource
      "event_object_type": "files", //resource name
      "event_new_path": response_body.events[i].data.target_path, //new path of the file, mainly used for documents hub
      "event_old_path": null //old path of the file, mainly used for documents hub
    };

    if (response_body.events[i].action + "" === "create") {
      eve.event_type = "CREATED";

      if (response_body.events[i].data.is_folder + "" === "false") {
        eve.event_object_id = response_body.events[i].data.target_group_id;
      } else {
        eve.event_object_type = "folders";
        eve.event_object_id = response_body.events[i].data.target_path;
      }
    } else if (response_body.events[i].action + "" === "copy") {
      eve.event_type = "CREATED";

      if (response_body.events[i].data.is_folder + "" === "false") {
        eve.event_object_id = response_body.events[i].data.target_group_id;
        eve.event_old_path = response_body.events[i].data.source_path;
      } else {
        eve.event_object_type = "folders";
        eve.event_object_id = response_body.events[i].data.target_path;
        eve.event_old_path = response_body.events[i].data.source_path;
      }
    } else if (response_body.events[i].action + "" === "delete") {
      eve.event_type = "DELETED";

      if (response_body.events[i].data.is_folder + "" === "false") {
        eve.event_object_id = response_body.events[i].data.target_group_id;
      } else {
        eve.event_object_type = "folders";
        eve.event_object_id = response_body.events[i].data.target_path;
      }
    } else if (response_body.events[i].action + "" === "move") {
      eve.event_type = "MOVED";

      if (response_body.events[i].data.is_folder + "" === "false") {
        eve.event_object_id = response_body.events[i].data.target_group_id;
        eve.event_old_path = response_body.events[i].data.source_path;
      } else {
        eve.event_object_type = "folders";
        eve.event_object_id = response_body.events[i].data.target_path;
        eve.event_old_path = response_body.events[i].data.source_path;
      }
    } else if (response_body.events[i].action + "" === "restore") {
      eve.event_type = "RETRIEVED";

      if (response_body.events[i].data.is_folder + "" === "false") {
        eve.event_object_id = response_body.events[i].data.target_group_id;
      } else {
        eve.event_object_type = "folders";
        eve.event_object_id = response_body.events[i].data.target_path;
      }
    } else {
      eve = null;
    }

    if (eve) {
      events.push(eve);
    }
  }

  done({
    "configuration": configuration,
    "response_body": events
  });
