      var providerViewLinkDate;
      var providerLinkDate;

      if (count !== "0") {
        for (var i = 0; i < parseInt(count); i++) {
          if (resp.links[i].protection + "" === 'PREVIEW') {
            var creation_date = "" + resp.links[i].creation_date;
            if (creation_date.length === 24) {
              creation_date = creation_date.substr(0, 22) + ":" + creation_date.substr(22);
            }
            if (!providerViewLinkDate || providerViewLinkDate < new Date(creation_date)) {
              response.providerViewLink = resp.links[i].url + "";
              response.providerViewLinkExpires = d;
              providerViewLinkDate = new Date(creation_date);
            }
          } else if (resp.links[i].protection + "" === 'NONE') {
            var creation_date = "" + resp.links[i].creation_date;
            if (creation_date.length === 24) {
              creation_date = creation_date.substr(0, 22) + ":" + creation_date.substr(22);
            }
            if (!providerLinkDate || providerLinkDate < new Date(creation_date)) {
              response.providerLink = resp.links[i].url + "";
              response.providerLinkExpires = d;
              providerLinkDate = new Date(creation_date);
            }
          }
        }

        if (response.providerViewLink && !response.providerLink) {
          response.providerLink = response.providerViewLink + "";
          response.providerLinkExpires = d;
        } else if (response.providerLink && !response.providerViewLink) {
          response.providerViewLink = response.providerLink + "";
          response.providerViewLinkExpires = d;
        } else if (!response.providerViewLink && !response.providerLink) {
          response.providerLink = providerLink;
          response.providerLinkExpires = d;
        }
      } else {
        response.providerLink = providerLink;
        response.providerLinkExpires = d;
      }

      done({
        "response_body": response,
        "continue": false
      });
    } catch (e) {
