//metadata has "fields" : [ {"type": "val", "vendorPath": "name"} ]
//types - string, number, boolean
function filterMetadata(arrayMap) {
	var finalArray = [];
	for (var i = 0; i < arrayMap.length; i++) {
		let tmp = arrayMap[i];
		let tmpMap = {};
		if (tmp.compositeFields) {
			let obj = tmp.compositeFields;
			if (obj.compositeField) {
				let array = obj.compositeField;
				for (var j = 0; j < array.length; j++) {
					let mapper = array[j];
					let newObj = {};
					var pathAttr = tmp.label + ". " + mapper['@key'];
					newObj.vendorPath = correctName(pathAttr);
					newObj.type = 'string';
					finalArray.push(newObj);
				}
			}
		} else {
			if (tmp.label) {
				tmpMap.vendorPath = correctName(tmp.label);
			}
			if (tmp['@base_type']) {
				tmpMap.type = determineType(tmp['@base_type']);
			}	
			finalArray.push(tmpMap);
		}
	}

	return finalArray;
}

function correctName(name) {
	var flag = false;
	while(flag === false) {
		if (name.indexOf(" ") > -1) {
			name = name.replace(" ", "_");
		} else if (name.indexOf("#") > -1) {
			name = name.replace("#", "_");
		} else if (name.indexOf("/") > -1) {
			name = name.replace("/", "_");
		} else if (name.indexOf("?") > -1) {
			name = name.replace("?", "_");
		} else if (name.indexOf(".") > -1) {
			name = name.replace(".", "_");
		} else {
			flag = true;
		}
	}
	name = convertNonAlphaNumeric(name);
	return name.toLowerCase();
}

function convertNonAlphaNumeric(fieldName) {
	fieldName = fieldName.replace(/\W+/g, "_");  
	return fieldName;
}

function determineType(baseType) {
	switch(baseType) {
		case 'text':
			return 'string';
		case 'bool':
			return 'boolean';
		case 'int32':
		case 'int64':
			return 'number';
		default: 
			return 'UNKNOWN';
	}
}


