// This function replaces the request values in res to construct a new Map
function replaceValues(res, req) {
  if(!res) {
    return req;
  }
  Object.keys(req).forEach(k => {
    res[k] = typeof req[k] === 'object' ? replaceValues(res[k], req[k]) : req[k];
  });
  return res;
}

// Create a new MAp for X
function replaceToX(req) {
  if(!req) {
    return req;
  }

  let newReq = {};
  Object.keys(req).forEach(k => {
    newReq[k] = 'X';
  });
  return newReq;
}

