// This function is to clean up any null/undefined values from object
function cleanupObject(obj) {
  let reducedObj = Object.keys(obj).reduce(function(newObj, key) {
    if(obj[key]) {
        newObj[key] = obj[key];      
    }
  	return newObj;  
	}, {});
  return reducedObj;
}

//Remove keys thats are not in the requestBody
let reducedNewResponse = Object.keys(newResponse).reduce(function(obj, key) {
  if(requestBody[key]){
    if(newResponse[key]) {

      //Remove null keys from object
      obj[key] = typeof newResponse[key] === 'object' ? cleanupObject(newResponse[key]): newResponse[key];      
    }
  }  
  return obj;  
}, {});

// Create a new X object
let newX = Object.keys(requestBody).reduce(function(obj, key) {
 	if (typeof requestBody[key] === 'object') {
	  obj[`${key}X`] = replaceToX(requestBody[key]) 
  }
  return obj;
}, {});

