let idocs = events.changes;

let status = {
  "003" : "DELETED",
  "004" : "UPDATED",
  "005" : "UPDATED",
  "009" : "CREATED",
  "023" : "UPDATED",
  "018" : "UPDATED"
}

let processedEvents = [];

idocs.forEach(i => {
  let debmasKey = null;
  let objectKey = null;
	Object.keys(i).forEach(k => {
    	if(k.indexOf('DEBMAS') > -1) {
         debmasKey = k;
      } else if(!(k.indexOf('DEBMAS') > -1 || k.indexOf('createdDate') > -1 || k.indexOf('tid') > -1)) {
         objectKey = k;
      }
  });

  let event = null;
  if(debmasKey) {
    let E1KNA1M = i[debmasKey].IDOC.E1KNA1M
    event = {
      "event_date" : i.createdDate,
      "event_type" : status[E1KNA1M.MSGFN],
      "event_object_id" : E1KNA1M.KUNNR,
      "event_object_type" : "customers",
    }
  } else if(objectKey) {
    event = {
      "event_date" : i.createdDate,
      "event_type" : "UNKNOWN",
      "event_object_id" : i.tid,
      "event_object_type" : objectKey,
    }    
  }

  if(event) {
    processedEvents.push(event)
  }

})

done({
  "events": processedEvents
});
