var formattedEvents = [];
var eventObj = {}; 
var para; 
var actualVal; 
var header = events['eventHeaders']; 
var xml = "text/xml; charset=utf-8"; 
var json = "application/json; charset=utf-8"; 
if (header['content-type'] === xml) { 
    if (events['callout']) { 
        var bodyData = events['callout']; 
        para = bodyData['parameter']; 
        var name, actualVal, value; 
        for (var i = 0; i < para.length; i++) { 

            var rec = para[i]; 

            if (rec.hasOwnProperty('@name')) { 
                actualVal = rec['$']; 
                name = rec['@name']; 

            } else { 
                var value = rec['$']; 
                name = value['name']; 
                actualVal = rec['_']; 
            } 

            if (name === 'EventCategory') { 
                console.log(actualVal); 
                var type = actualVal.substr(actualVal.length - 7); 
                var key = actualVal.substr(actualVal.length, 6); 
                switch (actualVal) { 
                    case 'InvoicePosted': 
                        eventObj.event_type = "CREATED"; 
                        eventObj.event_object_type = 'Invoice'; 
                        break; 
                    case 'InvoiceDue': 
                        eventObj.event_type = "CREATED"; 
                        eventObj.event_object_type = "Invoice"; 
                        break; 

                    case 'SubscriptionCreated': 
                        eventObj.event_type = "CREATED"; 
                        eventObj.event_object_type = 'Subcription'; 
                        break; 
                    case 'PaymentMethodUpdated': 
                        eventObj.event_type = "UPDATED"; 
                        eventObj.event_object_type = 'PaymentMethod'; 
                        break; 
                    case 'InvoicesPastDueAccountSummary': 
                        eventObj.event_type = "RETRIEVED"; 
                        eventObj.event_object_type = 'Invoices'; 
                        break; 
                    default: 
                        eventObj.event_type = 'UNKNOWN'; 
                        eventObj.event_object_type = actualVal; 

                } 
            } 
            if (name === 'EventTimestamp' || name === 'EventDate') { 
                eventObj.event_date = actualVal; 

            } 
            if (name === 'InvoiceId' || name === 'AccountAccountNumber' || name === 'AccountID' || name === 'SubscriptionID') { 
                eventObj.event_object_id = actualVal; 

            } 
        } 

    } 
} else if (header['content-type'] === json) { 
    var eventBody = events; 
    for (var key in eventBody) { 
        actualVal = eventBody[key]; 
        if (key === 'EventCategory') { 
            switch (actualVal) { 
                case 'InvoicePosted': 
                    eventObj.event_type = "CREATED"; 
                    eventObj.event_object_type = 'Invoice'; 
                    break; 
                case 'InvoiceDue': 
                    eventObj.event_type = "CREATED"; 
                    eventObj.event_object_type = "Invoice"; 
                    break; 

                case 'SubscriptionCreated': 
                    eventObj.event_type = "CREATED"; 
                    eventObj.event_object_type = 'Subcription'; 
                    break; 
                case 'PaymentMethodUpdated': 
                    eventObj.event_type = "UPDATED"; 
                    eventObj.event_object_type = 'PaymentMethod'; 
                    break; 
                case 'InvoicesPastDueAccountSummary': 
                    eventObj.event_type = "RETRIEVED"; 
                    eventObj.event_object_type = 'Invoices'; 
                    break; 
                default: 
                    eventObj.event_type = 'UNKNOWN'; 
                    eventObj.event_object_type = actualVal; 

            } 
        } 
        if (key === 'EventTimestamp' || key === 'EventDate') { 

            eventObj.event_date = eventBody[key]; 

        } 
        if (key === 'InvoiceId' || key === 'AccountAccountNumber' || key === 'AccountID' || key === 'SubscriptionID') { 
            eventObj.event_object_id = eventBody[key]; 

        } 
    } 
} 
formattedEvents.push(eventObj); 
done({ 
    "events": formattedEvents 
});
